import sys
def mod_exp(x, y, N):
    if y is 0:
        return 1
    z = mod_exp(x, y//2, N)
    if y % 2 is 0:
        return (z**2) % N
    return x * (z**2) % N

def gcd(a, b):
    s = 0
    t = 1
    r = b
    tempS = 1
    tempT = 0
    tempR = a
    while r != 0:
        q = tempR // r
        temp = r
        r = tempR - q * temp
        tempR = temp
        
        temp = s
        s = tempS - q * temp
        tempS = temp

        temp = t
        t = tempT - q * temp
        tempT = temp
    return tempR, tempS, tempT

